# version 1.0. images labelled a.b.c.d,# a is game, b is chain no, c is position in chain, d is name of person# version 2.0. images now labelled x.y# x = name of person who started chain# y = name of person who created that particular imageimport osfrom os import listdirimport shutilimport skimagefrom skimage import iofrom skimage.transform import resizeimport numpy as nppath_raw = "./raw_photos/"path_final = "./final_photos/"# to be manually updated for each gamea = 2names = ['t', 'p', 'l', 'r', 'a', 'i', 'j', 'd', 's', 'al']names.reverse()def rename_files():    for f in listdir(path_raw):        try:            x,y,end = f.split(".")            b = names.index(x)            c = (names.index(y) - b) % len(names)            new_name = f'{a:03}.{b:03}.{c:03}.jpg'            os.rename(path_raw+f, path_raw+new_name)        except:            os.remove(path_raw+f)    def combine_raw():        current_sequence = ""    black_line = np.zeros((5,1000,3))        for f in sorted(listdir(path_raw)):        #extract game num, sequence num and step num from file name        a,b,c,end = f.split(".")                #open image and rescale so it is 1000 pixels wide        image = io.imread(path_raw+f)        scale_factor = image.shape[1] /1000         image_resized = resize(image,                               (image.shape[0] // scale_factor, 1000),                               anti_aliasing=True)                #check if current image is part of previous sequence or new sequence        if current_sequence == "":            new_image = image_resized            current_sequence = a + b        elif a + b == current_sequence:            new_image = np.concatenate([new_image,                                        black_line,                                        image_resized], axis=0)        else:            io.imsave(path_final+'.'.join([current_sequence,end]), new_image)            current_sequence = a + b            new_image = image_resized    io.imsave(path_final+'.'.join([current_sequence,end]), new_image)rename_files()# combine_raw()